CREATE EXTENSION IF NOT EXISTS "uuid-ossp";CREATE TABLE IF NOT EXISTS inventory (    id              BIGSERIAL PRIMARY KEY,    sku             VARCHAR(255));CREATE TABLE IF NOT EXISTS location (    id              serial PRIMARY KEY,    name            VARCHAR(255),    type            VARCHAR(10),    address_line1   VARCHAR(255),    address_line2   VARCHAR(255),    city            VARCHAR(255),    postcode        VARCHAR(255));CREATE TABLE IF NOT EXISTS inventory_location (    inventory_id    BIGSERIAL REFERENCES inventory(id),    location_id     SERIAL REFERENCES location(id),    quantity        BIGSERIAL,    PRIMARY KEY(inventory_id, location_id));insert into location values(1, 'default', 'WAREHOUSE', '2a Quinton Street', '', 'London', 'SW18 3QS');CREATE INDEX IF NOT EXISTS location_name ON location (name);CREATE UNIQUE INDEX IF NOT EXISTS inventory_sku ON inventory (sku);